sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === oldValue) {
      return;
   }

	jslog('newValue:' + newValue); 
   var gaUser = new GlideAjax('GetEmailAddress_Sirisha');
   gaUser.addParam('sysparm_name', 'getEmail');
   gaUser.addParam('sysparm_userID', newValue);
	gaUser.getXML(setEmail);

	function setEmail(response){
		var email = response.responseXML.documentElement.getAttribute("answer");
		jslog('\n\nreturned email:' + email);
		g_form.setValue('u_requested_for_email', email);
	}



}
