sys_script_include.script:
var NeedItUtils_Sirisha = Class.create();
NeedItUtils_Sirisha.prototype = {
    initialize: function() {},

    isDatePast: function(strDate) {
        var rightNow = new GlideDateTime();
        var givenDateTime = new GlideDateTime(strDate);


        if (givenDateTime.before(rightNow)) {

            return true;

        } else if (givenDateTime.getDate() == rightNow.getDate()) {
            return true;
        } else return false;

    },

    type: 'NeedItUtils_Sirisha'
};
